INSERT INTO `teachings`(`title`, `subtitle`, `chapters`) VALUES (
  "Fanavotana ao amin'i Kristy",
  "Hebreo 9:12 \"...dia niditra indray mandeha ho any amin’ ny fitoerana masìna ka nahazo fanavotana mandrakizay\".\nFanavotana mandrakizay no azon'i Jesosy ho antsika!","[{\"title\":\"1. Jesosy - Andriamanitra mpamorona\",\"sections\":[{\"audioId\":\"fanavotana/jaona-1.mp3\",\"subtitle\":\"Jaona 1:1-3\",\"verses\":\"1 Tamin’ ny voalohany ny Teny, ary ny Teny tao amin’ Andriamanitra, ary ny Teny dia Andriamanitra. \\n2 Izy dia tao amin’ Andriamanitra tamin’ ny voalohany. \\n3 Izy no nahariana ny zavatra rehetra; ary raha tsy Izy dia tsy nisy nahariana izao zavatra ary izao, na dia iray aza.\",\"comment\":\"Ity Teny ity dia milaza fa I Jesosy dia Andriamanitra mpamorona: \\\"Teny dia Andriamanitra\\\", \\\"Izy no nahariana ny zavatra rehetra\\\".\"},{\"audioId\":\"fanavotana/hebreo-1.mp3\",\"subtitle\":\"Hebreo 1:1-4\",\"verses\":\"1. Rehefa nampitenenin’ Andriamanitra tamin’ ny razana fahiny ny mpaminany tamin’ ny andro samy hafa sy ny fanao samy hafa; \\n2 dia amin’ izao andro farany izao kosa no nampitenenany tamintsika ny Zanany, Izay voatendriny ho Mpandova ny zavatra rehetra; Izy no nahariany izao tontolo izao, \\n3 Izy koa no famirapiratan’ ny voninahiny sy endriky ny tenany indrindra ary mihazona ny zavatra rehetra amin’ ny herin’ ny teniny; ary rehefa nanao fanadiovana hahafaka ny fahotana Izy, dia nipetraka teo amin’ ny tanana ankavanan’ ny Lehibe any amin’ ny avo \\n4 ka natao tsara noho ny anjely, araka ny nandovany anarana tsara noho ireny.\",\"comment\":\"Milaza ireo andininy ireo fa i Jesosy dia tsy mitovy amin'ny mpaminany sy ny anjely. Izy dia ilay Zanak'Andriamanitra.\\nNy mahasamihafa Azy:\\n- Izy no nahariana izao tontolo izao: izany hoe tsy zava-boahary Izy fa mpamorona\\n- famirapiratry ny voninahitra sy endriky ny Ray: naneho ny natioran'Andriamanitra feno tamintsika Izy.\\n- mihazona ny zavatra rehetra amin’ ny herin’ ny teniny: Sady mpamorona izy no mihazona izay noforonony.\"}],\"questions\":[{\"key\":\"1\",\"question\":\"I Jesosy dia:\",\"options\":[\"Noforonina niaraka tamin'izao tontolo izao\",\"Tsy nisy talohan'ny niterahan'i Maria Azy\",\"Efa nisy hatramin'ny voalohany sady Andriamanitra mpamorona\"],\"response\":\"Efa nisy hatramin'ny voalohany sady Andriamanitra mpamorona\"}]}]")
